package basha;

public class ArrayList {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<int> a11 = new ArrayList<int>();
		System.out.println("Initial size of ArrayList: "+ a11.size());
		a11.add(25);
		a11.add(35);
		a11.add(45);
		a11.add(55);
		a11.add(555);
		System.out.println("\n size of ArrayList: "+a11.size());
		System.out.println("\n contents of ArrayList: "+ a11);

	}

}
